import React, { useEffect, useRef, useCallback, useState } from 'react'
import { useQuery, useMutation } from '@apollo/react-hooks'
import {
  USER_QUERY,
  PROJECT_QUERY
} from './graphql'
import './App.css'
//import React, { Component } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "./App.css";
import "react-big-calendar/lib/css/react-big-calendar.css";

const localizer = momentLocalizer(moment);

function App() {
  const [projects, setProjects] = uesState([])
  const [events, setEvents] = useState([])
  const [items, setItems] = useState([])
  const [username, setUsername] = useState('May')
  const [userid, setUserid] = useState('1111')
  const [type, setType] = useState('login') 
  const { userdata, refetch, subscribeToMore } = useQuery(USER_QUERY, {
      variables: {
          query: {
              name: username,
              id: userid,
              type: type
          }
      }
  })

  useEffect(() => {
    console.log(userdata)
    setProjects(userdata.user.projects)
    var itemlist = []
    projects.forEach(p => {
      p.events.forEach(e => {
        e.items.forEach(i => {
          itemlist.push({
            start: i.time.start,
            end: i.time.end,
            title: p.name + ' ' + e.name + ' ' + i.name
          })
        })
      })
    })
    console.log(itemlist)
    setItems(itemlist)

  }, [data])

